      } else {}
    }
  }

  @override
  Widget build(BuildContext context) {
    double heigh = MediaQuery.of(context).size.height;
    double width = MediaQuery.of(context).size.width;

    return WillPopScope(
      onWillPop: _onBackPressed,
      child: Scaffold(
        backgroundColor: Color(0xFF5e5b52),
        body: StreamBuilder(
            stream: Firestore.instance
                .collection('users')
                .where('email', isEqualTo: widget.userEmail)
                .snapshots(),
            builder: (context, snapshot) {
              if (!snapshot.hasData) {
                return Container();
              } else {
                DateTime d = snapshot.data.documents[0].data['DOB'].toDate();
                DateTime today = DateTime.now();
                AgeDuration age;

                // Find out your age
                age = Age.dateDifference(
                    fromDate: d, toDate: today, includeToDate: false);
                getAstrology(
                    d.month, d.day, snapshot.data.documents[0].data['isMale']);

                return Container(
                  child: Stack(
                    children: [
                      galaryIcon(imageSourceVisible, _takeFromGalary),
                      cameraIcon(imageSourceVisible, _takePicture),
                      bigImage(heigh, snapshot.data.documents[0].data['image']),
                      widget.roomID == null
                          ? myImageProfile(choosrImageSource,
                              snapshot.data.documents[0].data['image'])
                          : userImageProfile(
                              snapshot.data.documents[0].data['image']),
                      userinfo(
                        context,
                        widget.userName,
                        snapshot.data.documents[0].data['isMale'],
                        age.years.toString(),
                        astrology,
                        astrologyDiscribe,
                      ),
                      widget.roomID == null
                          ? myProfile()
                          : userProfileButtonToChatConversation(
                              context,
                              widget.userName,
                              widget.roomID,
                              widget.userEmail,
                              widget.userImage,
                              snapshot.data.documents[0].data['isMale'],
                            ),
                    ],
                  ),
                );
              }
            }),
      ),
    );
  }

  choosrImageSource() {
    setState(() {
      imageSourceVisible = !imageSourceVisible;
    });
  }

  File imageStored;
  String urlImage;

  final picker = ImagePicker();
  _takeFromGalary() async {
    final pickedFile = await picker.getImage(
        source: ImageSource.gallery, imageQuality: 100, maxWidth: 1200);
    setState(() {
      try {
        imageStored = File(pickedFile.path);
      } catch (e) {}
    });
    uploadImage();
    imageSourceVisible = false;
  }

  _takePicture() async {
    final pickedFile = await picker.getImage(
        source: ImageSource.camera, imageQuality: 100, maxWidth: 1200);
    setState(() {
      try {
        imageStored = File(pickedFile.path);
      } catch (e) {}
    });
    uploadImage();
    imageSourceVisible = false;
  }

  Future uploadImage() async {
    String fileName = '${DateTime.now()}.png';

    StorageReference firebaseStorage =
        FirebaseStorage.instance.ref().child(fileName);

    StorageUploadTask uploadTask = firebaseStorage.putFile(imageStored);
    await uploadTask.onComplete;
    urlImage = await firebaseStorage.getDownloadURL() as String;

    if (urlImage.isNotEmpty) {
      Database().getUserID(Constanse.myEmail).then((v) {
        v.documents.forEach((result) async {
          Firestore.instance
              .collection('users')
              .document(result.documentID)
              .updateData({
            "image": urlImage,
          });

          HelperFunction.saveUserImage(urlImage);
          Constanse.myImage = await HelperFunction.getUserImage();
          setState(() {});
        });
      });
    }
  }
}
